import {useState} from "react";

const MultiCheckbox = () => {
    const [checkBoxes, setCheckBoxes] = useState({check1: false, check2: false, check3: false})

/* 
    const handleChange =(e) => {
        setCheckBoxes({...checkBoxes, [e.target.name]: e.target.checked})
    } //then call it on the onchange attribute!!! same code of the inline function */


    return (
        <div>
            <input name="check1" type="checkbox" checked={checkBoxes.check1} onChange={(e) => setCheckBoxes(e.target.checked)} />
            <input name="check2" type="checkbox" checked={checkBoxes.check2} onChange={(e) => setCheckBoxes(e.target.checked)} />
            <input name="check3" type="checkbox" checked={checkBoxes.check3} onChange={(e) => setCheckBoxes(e.target.checked)} />
        </div>
    )
}


export default MultiCheckbox
